import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {BarChart} from 'react-d3-components';
import Slider from "react-slick";
import * as d3 from "d3";
import {HorizontalBar,Pie} from 'react-chartjs-2';
//var ReactCSSTransitionGroup = require('react-addons-css-transition-group');
  const data_1 = [
    {
    label: 'somethingA',
    values: [{x: '2013-2014', y: 39}, {x: '2014-2015', y: 34.5}, {x: '2015-2016', y: 41.2}, {x: '2016-2017', y: 31.4}]
    },
    {
    label: 'somethingB',
    values: [{x: '2013-2014', y: -5.7}, {x: '2014-2015', y: -16.7}, {x: '2015-2016', y:-1.5}, {x: '2016-2017', y: 13.6}]
    },
    {
    label: 'somethingC',
    values: [{x: '2013-2014', y: 1.4}, {x: '2014-2015', y: -2.9}, {x: '2015-2016', y: 2.3}, {x: '2016-2017', y:10.9}]
    },
    {
    label: 'somethingD',
    values: [{x: '2013-2014', y: 6.8}, {x: '2014-2015', y: 6.5}, {x: '2015-2016', y: 6.3}, {x: '2016-2017', y:6.6}]
    },
    {
    label: 'somethingE',
    values: [{x: '2013-2014', y: 2.9}, {x: '2014-2015', y: 2.9}, {x: '2015-2016', y: 2.5}, {x: '2016-2017', y: 3.2}]
    }
  ];
  const data_2= {
  labels: ['for SMEs using PayPal','for offline PayPal mirror basket','for overall offline exports'],
  datasets: [
    {
      
      backgroundColor: 'rgb(68, 114, 196)',
      borderColor: 'rgb(68, 114, 196)',
      borderWidth: 1,
      hoverBackgroundColor: 'rgb(68, 114, 196)',
      hoverBorderColor: 'rgb(68, 114, 196)',
      data: [29,-6,-4]
     
    }
  ]
 
    
};
const data_3 = {
	labels: [
    'inclumbent small business using PayPal',
		'new entrants using PayPal'
	],
	datasets: [{
		data: [45, 55],
		backgroundColor: [
      '#4472c4',
	   	'#ed7d31'
		]
	}]
};

class India extends React.Component {
    constructor(props) {
        super(props);
        this.play = this.play.bind(this);
        this.pause = this.pause.bind(this);
        this.state = { width: window.innerWidth, }; // responsive mobile
        
      }
     
      play() {
        this.slider.slickPlay();
      }
      pause() {
        this.slider.slickPause();
      }
    // responsive mobile Start
    componentWillMount() {
      window.addEventListener('resize', this.handleWindowSizeChange);
    }

    // make sure to remove the listener
    // when the component is not mounted anymore
    componentWillUnmount() {
      window.removeEventListener('resize', this.handleWindowSizeChange);
    }

    handleWindowSizeChange = () => {
      this.setState({ width: window.innerWidth });
    };  

// responsive mobile end...  
    render(){
        const settings = {
            dots: true,
            infinite: true,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoplay: true,
            autoplaySpeed: 1000
          };
          const { width } = this.state; // responsive mobile
		      const isMobile = width <= 500; // responsive mobile
          if (isMobile) {
            return(
              <div>
               
                      <Slider ref={slider => (this.slider = slider)} {...settings}>
                          <div className="br_sldr_upr">
                            <h2>Change in number of trade partners between 2013 and 2017</h2>
                            <div className="bar_graph bar_sldr">
                              {/* <h2 style={{textAlign: 'center'}}>Change in number of trade partners between 2013 and 2017</h2> */}
                             <HorizontalBar data={data_2} />
                            </div>
                          </div>
                          <div className="br_sldr_upr">
                            <h2>Growth rate comparison</h2>
                            <div className="bar_graph bar_sldr">
                              {/* <h2 style={{textAlign: 'center'}}>Change in number of trade partners between 2013 and 2017</h2> */}
                              <BarChart
                              groupedBars
                              data={data_1}
                              width={320}
                              height={400}
                              margin={{top: 10, bottom: 50, left: 50, right: 10}}
                              xAxis={{innerTickSize: 100}}
                              yAxis={{tickFormat:function(d) { return d3.format('d')(d*1) +'%'; } }}/>
                            </div>
                          </div>
                          <div className="br_sldr_upr">
                            <h2>contribution to growth in export volume using PayPal (2013-2017)</h2>
                            <div className="bar_graph bar_sldr">
                          <Pie data={data_3} />
                            </div>
                          </div>
                          
                      </Slider>
                      
                      <div className="ply_pause_btn" style={{ textAlign: "center" }}>
                        <div className="both_ply_pause">
                          <button className="button" onClick={this.play}>
                            <img src="image/play_button.png" />
                          </button>
                          <button className="button pause_btn" onClick={this.pause}>
                            <img src="image/pause_button.png" />
                          </button>
                        </div>
                      </div>
                    </div>           
            );
          }
        
        else{
          return(
            <div>
             
                    <Slider ref={slider => (this.slider = slider)} {...settings}>
                        <div className="br_sldr_upr">
                          <h2>Change in number of trade partners between 2013 and 2017</h2>
                          <div className="bar_graph bar_sldr">
                            {/* <h2 style={{textAlign: 'center'}}>Change in number of trade partners between 2013 and 2017</h2> */}
                           <HorizontalBar data={data_2} />
                          </div>
                        </div>
                        <div className="br_sldr_upr">
                          <h2>Growth rate comparison</h2>
                          <div className="bar_graph bar_sldr">
                            {/* <h2 style={{textAlign: 'center'}}>Change in number of trade partners between 2013 and 2017</h2> */}
                            <BarChart
                            groupedBars
                            data={data_1}
                            width={680}
                            height={400}
                            margin={{top: 10, bottom: 50, left: 50, right: 10}}
                            xAxis={{innerTickSize: 100}}
                            yAxis={{tickFormat:function(d) { return d3.format('d')(d*1) +'%'; } }}/>
                          </div>
                        </div>
                        <div className="br_sldr_upr">
                          <h2>contribution to growth in export volume using PayPal (2013-2017)</h2>
                          <div className="bar_graph bar_sldr">
                        <Pie data={data_3} />
                          </div>
                        </div>
                        
                    </Slider>
                    
                    <div className="ply_pause_btn" style={{ textAlign: "center" }}>
                      <div className="both_ply_pause">
                        <button className="button" onClick={this.play}>
                          <img src="image/play_button.png" />
                        </button>
                        <button className="button pause_btn" onClick={this.pause}>
                          <img src="image/pause_button.png" />
                        </button>
                      </div>
                    </div>
                  </div>           
          );
        }
    }
} 
export default India;
